package com.pandora.algorithm.magic;/** * Created by mordor on 17/6/22. */public class NineMagic {    public static void main(String[] args) {        int finalNum = 9;        for(int i = 1 ; i < 10 ; i++ ){            int x = i * finalNum;            System.out.print(x + " = (");            System.out.print((x/10 +" + "+ x%10) + ") / "+finalNum+" = ");            System.out.println((x/10 + x%10) / finalNum);        }    }}