package com.pandora.basic.thread.basis;import java.util.concurrent.TimeUnit;/** * Created by mordor on 17/6/28. */public class TryThreadSimple {    public static void main(String[] args) {        Runnable runnable = () -> {            try {                System.out.println("FOO : " + Thread.currentThread().getName());                TimeUnit.SECONDS.sleep(1);                System.out.println("BAR : " + Thread.currentThread().getName());            } catch (InterruptedException e) {                e.printStackTrace();            }        };        Thread thread = new Thread(runnable);        thread.start();        Thread thread1 = new Thread(runnable);        thread1.start();    }}